import React from 'react';
import { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { getproblemsuper, status_update } from '../utils/ApiRequest';
import axios from 'axios';
import { Button } from 'react-bootstrap';
import './complaint.css';

function Complaint({ handle_view }) {
  const navigate = useNavigate();
  const [refresh, setRefresh] = useState(false);
  const [problems, setProblems] = useState([]);
  const [currId, setCurrId] = useState(null);
  const [show, setShow] = useState(false);
  const [status, setStatus] = useState('');

  const handleViewClick = (image) => {
    handle_view(image);
    navigate('/image');
  };

  useEffect(() => {
    const fetchProblems = async () => {
      try {
        const { data } = await axios.post(getproblemsuper);
        setRefresh(true);

        const currentTime = new Date();
        const filteredProblems = data.problem.filter(problem => {
          const problemTime = new Date(problem.createdAt);
          const timeDifference = currentTime - problemTime;
          return timeDifference > 300000 && problem.status === 'pending';
        });
        filteredProblems.forEach(problem => {
          console.log('Complaint forwarded to superadmin:', problem);
        });
        setProblems(filteredProblems);
      } catch (error) {
        console.error('Error fetching problems:', error);
      }
    };

    if (localStorage.getItem("super")) {
      fetchProblems();
    } else {
      navigate("/superadminlogin");
    }
  }, [refresh, navigate]);

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await axios.post(status_update, { status, id: currId });

      if (data.success === true) {
        handleClose();
        setRefresh(!refresh);
        window.location.reload();
      } else {
        console.log("Error updating status:", data.message);
      }
    } catch (error) {
      console.error('Error updating status:', error);
    }
  };

  const handleClose = () => {
    setShow(false);
  };

  const handleShow = () => {
    setShow(true);
  };

  const handleEditClick = (id) => {
    setCurrId(id);
    handleShow();
  };

  const handleShowLogout = () => {
    localStorage.removeItem("admin");
    navigate("/adminlogin");
  };

  return (
    <div>
      <header className='navbar'>
        <Link to='/superadmin' className='navbar__title navbar__item superadmin-home'>Super Admin Panel</Link>
        <Button variant='primary' onClick={handleShowLogout} className="ml-2">Logout</Button>
      </header>
      <div className="report-container">
        <table className="report-table">
          <thead>
            <tr>
              <th>Complaint Lodged On</th>
              <th>Complaint Lodged By</th>
              <th>Subject</th>
              <th>Status</th>
              <th>Image</th>
              <th>Action</th>
              <th>View Details</th>
            </tr>
          </thead>
          <tbody>
            {problems.map(item => (
              <tr key={item.id}>
                <td>{item.createdAt}</td>
                <td>{item.email}</td>
                <td>{item.problem}</td>
                <td>{item.status}</td>
                <td><button onClick={() => handleViewClick(item.Image)}>View</button></td>
                <td>
                  <div className="icons-handle">
                    {!show && (
                      <button
                        key={item._id}
                        className="btn btn-warning"
                        onClick={() => handleEditClick(item._id)}
                      >
                        Open
                      </button>
                    )}
                    {show && (
                      <div className="openform">
                        <form onSubmit={handleEditSubmit}>
                          <label htmlFor="">Remarks</label>
                          <input
                            type="text"
                            name='status'
                            placeholder='Write it here..'
                            value={status}
                            onChange={(e) => { setStatus(e.target.value) }}
                          />
                          <button type='button' onClick={handleClose} className='btn btn-warning m-3'>close</button>
                          <button type='submit' className='btn btn-success'>submit</button>
                        </form>
                      </div>
                    )}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Complaint;
