import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useEffect, useState } from 'react';
import Register from './auth/Register';
import Login from './auth/Login';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Adminlogin from './auth/Adminlogin';
import Adminregistration from './Components/Adminregistration';
import Superadmin from './auth/Superadmin';
import Dashboard from './Dashboard';
import Newgrievance from './Newgrievance';
import Adminmaster from './Adminmaster';
import View from './View';
import Complaint from './Components/Complaint';
import Home from './Home';
import Dashboardsuperadmin from './Dashboardsuperadmin';
import Manageusers from './Manageusers';
import Footer from './Footer/Footer';
import Navbar from './Navbar/Navbar';
import Sidebar from './Sidebar/sidebar.jsx'

function App() {
  const[image,setimage]=useState(null);
  const [userRole, setUserRole] = useState('user'); // 'user' by default

  // Function to set the user's role
  const setUser = (role) => {
    setUserRole(role);
  };
  const handle=(name)=>{
    setimage(name);
    console.log(name);
  }
  const [showSidebar, setShowSidebar] = useState(false);

  const toggleSidebar = () => {
    setShowSidebar(!showSidebar);
  };

  useEffect(() => {
    // Fetch the user's role from the server upon authentication
    const fetchUserRole = async () => {
      try {
        // Make an API request to fetch the user's role
        const response = await fetch('/api/auth', {
          method: 'GET',
          credentials: 'include', // Include cookies for authentication
        });
        if (response.ok) {
          const data = await response.json();
          setUserRole(data.role); // Set the user's role in the state
        }
      } catch (error) {
        console.error('Error fetching user role:', error);
        // Handle error (e.g., redirect to an error page)
      }
    };

    fetchUserRole(); // Call the function to fetch user role upon component mount
  }, []);

  const history = useHistory();

  // Function to handle login

    // Perform authentication logic here
    // Upon successful authentication, set loggedIn to true and store user role
    setUser('superadmin'); // Set user role to 'superadmin'

  return ( 
    <>  
    <div class="area" >
            <ul class="circles">
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
            </ul>

    <div class="context">
    <Router>
        <Navbar toggleSidebar={toggleSidebar}/>
        <Sidebar showSidebar={showSidebar} toggleSidebar={toggleSidebar} userRole={"superadmin"}/>
        <Routes>
        <Route path='/' element={<Home/>}/>
        <Route path='/history' element={<Dashboard/>}/>
        <Route path='/new' element={<Newgrievance/>}/>
        <Route path='/complaint' element={<Complaint handle_view={handle}/>}/>
        <Route path='/create-admin' element={<Adminregistration/>} />
        <Route path='/superadmin' element={<Adminmaster handle_view={handle}/>}/>
        <Route path='/dashboards-a' element={<Dashboardsuperadmin/>}/>
        <Route path='/manage-users' element={<Manageusers/>}/>
        <Route path="/image" element={<View image={image}/>} />
        </Routes>
        <Footer/>
      </Router>
    </div>
    <Router>
        <Routes>
        <Route path='/superadminlogin' element={<Superadmin/>}/>
        <Route path='/adminlogin' element={<Adminlogin/>}/>
        <Route path="/register" element={<Register/>}/>
        <Route path="/login" element={<Login/>}/>  
        </Routes>
      </Router>
    </div >
    </>
  );
}

export default App;
