import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';
import { raiseproblem, add_image } from './utils/ApiRequest';
import { Form, Button, Container, Row, Col } from 'react-bootstrap';

import { Link, useHistory } from 'react-router-dom';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';
import '../src/new.css';

function NewGrievance() {
  const [image, setImage] = useState(null);
  const [address, setAddress] = useState('');
  const [department, setDepartment] = useState('');
  const [subdepartment, setSubdepartment] = useState('');
  const [problem, setProblem] = useState('');
  const history = useHistory();

  const departments = {
    PWD: ['Road Management', 'Bridge Maintenance', 'Street Lighting', 'Drainage and Sewerage'],
    HSD: ['Waste Management', 'Sanitation Services', 'Public Health Inspections','Vector Control'],
    TD: ['Public Transit', 'Traffic Management', 'Parking Services','Transportation Planning'],
    EPD: ['Air Quality Management', 'Water Quality Management', 'Hazardous Waste Management','Environmental Compliance'],
    SSD: ['Housing Assistance','Welfare Programs','Child and Family Services','Senior Services'],
    PSD: ['Police Services','Fire and Rescue Services','Emergency Management','Animal Control'],
  };

  const handleDepartmentChange = (e) => {
    setDepartment(e.target.value);
    setSubdepartment('');
  };

  function generateToken() {
    return uuidv4();
  }

  const handleShowLogout = () => {
    localStorage.removeItem('user');
    history.push('/login');
  };

  const handle_submit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('image', image);
    const result = await axios.post(add_image, formData, {
      headers: { 'Content-Type': 'multipart/form-data' },
    });
    const uniqueToken = generateToken();
    const user = JSON.parse(localStorage.getItem('user'));
    const data = await axios.post(raiseproblem, {
      email: user.email,
      address: address,
      problem: problem,
      department: department,
      token: uniqueToken,
    });
    if (data.data.success === true) {
      window.alert(`You Have raised your Problem and your Token is ${uniqueToken}`);
      history.push('/');
      toast.success(data.data.message);
    } else {
      toast.error(data.message);
    }
  };

  const reset = (e) => {
    e.preventDefault();
    setAddress('');
    setDepartment('');
    setSubdepartment('');
    setProblem('');
    setImage(null);
  };

  const onInputChange = (e) => {
    setImage(e.target.files[0]);
  };

  return (
    <Container>
      <Row className="justify-content-center">
        <Col md={8}>
          <div className="sidenavbar">
            <Link to="/">Home</Link>
            <Link to="/new">New Grievance</Link>
            <Button variant="primary" onClick={handleShowLogout} className="m-3">
              Logout
            </Button>
          </div>
          <h1 className="text-center mt-5">Raise Your Problem</h1>
          <Form className="mt-5" onSubmit={handle_submit}>
            <Form.Group controlId="formAddress">
              <Form.Label>Address</Form.Label>
              <Form.Control type="text" placeholder="Enter your address" value={address} onChange={(e) => setAddress(e.target.value)} />
            </Form.Group>
            <Form.Group controlId="formDepartment">
              <Form.Label>Department</Form.Label>
              <Form.Control as="select" value={department} onChange={handleDepartmentChange}>
                <option value="">Choose...</option>
                {Object.keys(departments).map((dept) => (
                  <option key={dept} value={dept}>
                    {dept}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
            {department && (
              <Form.Group controlId="formSubDepartment">
                <Form.Label>Sub Department</Form.Label>
                <Form.Control as="select" value={subdepartment} onChange={(e) => setSubdepartment(e.target.value)}>
                  <option value="">Choose...</option>
                  {departments[department].map((subDept) => (
                    <option key={subDept} value={subDept}>
                      {subDept}
                    </option>
                  ))}
                </Form.Control>
              </Form.Group>
            )}
            <Form.Group controlId="formImage">
              <Form.Label>Attach Image</Form.Label>
              <Form.Control type="file" accept="image/*" onChange={onInputChange} />
            </Form.Group>
            <Form.Group controlId="formProblem">
              <Form.Label>Problem Description</Form.Label>
              <Form.Control as="textarea" rows={3} value={problem} onChange={(e) => setProblem(e.target.value)} />
            </Form.Group>
            <Button variant="warning" type="submit" className="mr-2">
              Submit
            </Button>
            <Button variant="success" onClick={reset}>
              Reset
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
}

export default NewGrievance;
