import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Button, Link } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { getproblem, getUserInfo } from './utils/ApiRequest';

const Home = () => {
  const [userProblem, setUserProblem] = useState([]);
  const [userName, setUserName] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const user = JSON.parse(localStorage.getItem("user"));
        const { data: userInfo } = await axios.get(getUserInfo, {
          params: {
            userId: user._id,
          },
        });
        setUserName(userInfo.name);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    const fetchUserProblems = async () => {
      try {
        const user = JSON.parse(localStorage.getItem("user"));
        const { data: userProblems } = await axios.post(getproblem, {
          userId: user._id,
        });
        setUserProblem(userProblems.problem);
      } catch (error) {
        console.error('Error fetching user problems:', error);
      }
    };

    if (localStorage.getItem("user")) {
      fetchUserData();
      fetchUserProblems();
    } else {
      navigate("/login");
    }
  }, [navigate]);

  const handleShowLogout = () => {
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <>
      <div className="navbar">
        <h2>Home</h2>
        <h1>Grievance Management System</h1>
      </div>

      <div className="sidenavbar">
        <Link to="/">Home</Link>
        <Link to="/history">History</Link>
        <Link to='/new'>Grievance</Link>
        <Button variant='primary' onClick={handleShowLogout} className="btn btn-primary btn-block my-4">Logout</Button>
      </div>

      <div className="content">
        <div className="welcome">
          <h1>Welcome back, {userName}!</h1>

          <div className="user-problems">
            {userProblem.map((problem, index) => (
              <div key={index} className="problem-item">
                <p>{problem.description}</p>
                <p>Status: {problem.status}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;
