import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';
import { raiseproblem, add_image } from './utils/ApiRequest';
import { ToastContainer, toast } from 'react-toastify';
import { Form } from 'react-bootstrap';
import '../src/new.css';
import { Link } from 'react-router-dom';
import { Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Newgrievance() {
  const [image, setImage] = useState(null);
  const [address, setAddress] = useState('');
  const [department, setDepartment] = useState('');
  const [subdepartment, setSubdepartment] = useState('');
  const [problem, setProblem] = useState('');

  const departments = {
    PWD: ['Road Management', 'Bridge Maintenance', 'Street Lighting', 'Drainage and Sewerage'],
    HSD: ['Waste Management', 'Sanitation Services', 'Public Health Inspections','Vector Control'],
    TD: ['Public Transit', 'Traffic Management', 'Parking Services','Transportation Planning'],
    EPD: ['Air Quality Management', 'Water Quality Management', 'Hazardous Waste Management','Environmental Compilance'],
    SSD: ['Housing Assistance','Welfare Programs','Child and Family Services','Senior Services'],
    PSD: ['Police Services','Fire and Rescue Services','Emergency Management','Animal Control'],
  };

  const handleDepartmentChange = (e) => {
    setDepartment(e.target.value);
    setSubdepartment(''); // Reset sub department when department changes
  };

  function generateToken() {
    return uuidv4();
  }

  const navigate = useNavigate();
  const toastOptions = {
    position: 'bottom-right',
    autoClose: 2000,
    hideProgressBar: false,
    closeOnClick: true,
    pauseOnHover: false,
    draggable: true,
    progress: undefined,
    theme: 'dark',
  };

  const handleShowLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handle_submit = async (e) => {
    e.preventDefault();
    console.log(image);
    const formData = new FormData();
    formData.append('image', image);
    const result = await axios.post(add_image, formData, {
      headers: { 'Content-Type': 'multipart/form-data' },
    });
    const uniqueToken = generateToken();
    const user = JSON.parse(localStorage.getItem('user'));
    const data = await axios.post(raiseproblem, {
      email: user.email,
      address: address,
      problem: problem,
      department: department,
      token: uniqueToken,
    });
    if (data.data.success === true) {
      window.alert(`You Have raised your Problem and your Token is ${uniqueToken}`);
      window.location.reload();
      toast.success(data.data.message, toastOptions);
    } else {
      toast.error(data.message, toastOptions);
    }
  };

  const reset = (e) => {
    e.preventDefault();
    setAddress('');
    setDepartment('');
    setSubdepartment('');
    setProblem('');
    setImage(null);
  };

  const onInputChange = (e) => {
    setImage(e.target.files[0]);
  };

  return (
    <>
     <div class="sidenavbar">
  <Link to="/">Home</Link>
  <Link to="/new">New Grievance</Link>
  <Button variant="primary" onClick={handleShowLogout} className="m-3">
    Logout
  </Button>
</div>
<div class="content">
  <h1 class="text-center mt-5">Raise Your Problem</h1>
  <div class="container justify-content-center">
    <form class="form-new" method="POST">
      <div class="form-group">
        <label for="natureOfComplaint" class="h4">Nature of Complaint</label>
        <input
          type="text"
          class="form-control"
          id="natureOfComplaint"
          placeholder="Description of complaint..."
          value={address}
          onChange={(e) => setAddress(e.target.value)}
        />
      </div>
      <div class="form-group">
        <label for="department" class="h4">Department</label>
        <select
          class="form-control m-4"
          id="department"
          value={department}
          onChange={handleDepartmentChange}
        >
          <option value="">Choose...</option>
          <option value="PWD">Public Works Department</option>
          <option value="HSD">Health and Sanitation Department</option>
          <option value="TD">Transportation Department</option>
          <option value="EPD">Environmental Protection Department</option>
          <option value="SSD">Social Services Department</option>
          <option value="PSD">Public Safety Department</option>
        </select>
      </div>
      {department && (
        <div class="form-group">
          <label for="subDepartment" class="h4">Sub Department</label>
          <select
            class="form-control m-3"
            id="subDepartment"
            value={subdepartment}
            onChange={(e) => setSubdepartment(e.target.value)}
          >
            <option value="">Choose...</option>
            {departments[department] &&
              departments[department].map((subDept) => (
                <option key={subDept} value={subDept}>
                  {subDept}
                </option>
              ))}
          </select>
        </div>
      )}
      <div class="form-group">
        <label for="attachImage" class="h4">Attach Image</label>
        <input
          type="file"
          class="form-control-file"
          id="attachImage"
          accept="image/*"
          onChange={onInputChange}
        />
      </div>
      <div class="form-group">
        <label for="problem" class="h4">Problem</label>
        <input
          type="textarea"
          class="form-control-textarea"
          id="problem"
          placeholder="Problem Description"
          value={problem}
          onChange={(e) => setProblem(e.target.value)}
        />
      </div>
      <button type="submit" class="btn btn-warning" onClick={handle_submit}>
        Submit
      </button>
      <button class="btn btn-success m-3" onClick={reset}>
        Reset
      </button>
    </form>
  </div>
</div>

    </>
  );
}

export default Newgrievance;
