import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';
import { raiseproblem, add_image } from './utils/ApiRequest';
import { toast } from 'react-toastify';
import { Form } from 'react-bootstrap';
import { Link, useHistory } from 'react-router-dom';
import axios from 'axios';

function NewGrievance() {
  const [image, setImage] = useState(null);
  const [address, setAddress] = useState('');
  const [department, setDepartment] = useState('');
  const [subdepartment, setSubdepartment] = useState('');
  const [problem, setProblem] = useState('');
  const [location, setLocation] = useState('');
  const history = useHistory();

  const departments = {
    PWD: ['Road Management', 'Bridge Maintenance', 'Street Lighting', 'Drainage and Sewerage'],
    HSD: ['Waste Management', 'Sanitation Services', 'Public Health Inspections','Vector Control'],
    TD: ['Public Transit', 'Traffic Management', 'Parking Services','Transportation Planning'],
    EPD: ['Air Quality Management', 'Water Quality Management', 'Hazardous Waste Management','Environmental Compliance'],
    SSD: ['Housing Assistance','Welfare Programs','Child and Family Services','Senior Services'],
    PSD: ['Police Services','Fire and Rescue Services','Emergency Management','Animal Control'],
  };

  const handleDepartmentChange = (e) => {
    setDepartment(e.target.value);
    setSubdepartment(''); // Reset sub department when department changes
  };

  function generateToken() {
    return uuidv4();
  }

  const handleLogout = () => {
    localStorage.removeItem('user');
    history.push('/login');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('image', image);
    try {
      const result = await axios.post(add_image, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      const uniqueToken = generateToken();
      const user = JSON.parse(localStorage.getItem('user'));
      const data = await axios.post(raiseproblem, {
        email: user.email,
        address: address,
        problem: problem,
        department: department,
        token: uniqueToken,
      });
      if (data.data.success === true) {
        window.alert(`You Have raised your Problem and your Token is ${uniqueToken}`);
        window.location.reload();
        toast.success(data.data.message);
      } else {
        toast.error(data.message);
      }
    } catch (error) {
      console.error('Error submitting form:', error);
      toast.error('An error occurred while submitting the form.');
    }
  };

  const handleReset = () => {
    setDepartment('');
    setSubdepartment('');
    setLocation('');
    setProblem('');
    setImage(null);
  };

  const handleInputChange = (e) => {
    setImage(e.target.files[0]);
  };

  return (
    <>
      <header className='navbar'>
        <div className='navbar__title navbar__item'>User Panel</div>
        <h1 className="text-center">Raise Your Problem</h1>
      </header>
      <div className="sidenavbar">
        <Link to="/">Home</Link>
        <Link to="/history">History</Link>
        <Link to="/new">Grievance</Link>
        <button type="button" className="btn btn-primary btn-block my-4" onClick={handleLogout}>Logout</button>
      </div>
      <div className='container-new justify-content-center'>
        <form className="form-new" onSubmit={handleSubmit}>

          <label className="h4" htmlFor="departmentSelect">
            Department
          </label>
          <select
            className="mb-3 w-100"
            id="departmentSelect"
            value={department}
            onChange={handleDepartmentChange}
          >
            <option value="">Choose...</option>
            {Object.keys(departments).map((dept) => (
              <option key={dept} value={dept}>{dept}</option>
            ))}
          </select>

          <label className="h4" htmlFor="subdepartmentSelect">
            Sub Department
          </label>
          <select
            className="mb-3 w-100"
            id="subdepartmentSelect"
            value={subdepartment}
            onChange={(e) => setSubdepartment(e.target.value)}
          >
            <option value="">Choose...</option>
            {departments[department] &&
              departments[department].map((subDept) => (
                <option key={subDept} value={subDept}>
                  {subDept}
                </option>
              ))}
          </select>

          <label className="h4" htmlFor="problemDescription">
            Problem
          </label>
          <input
            type="textarea"
            className="form-control"
            id="problemDescription"
            placeholder="Problem Description"
            value={problem}
            onChange={(e) => setProblem(e.target.value)}
          />

          <Form.Group controlId="formFile" className="">
            <Form.Label className='h4'>Attach File</Form.Label>
            <Form.Control type="file" accept="image/*" onChange={handleInputChange} />
          </Form.Group>

          <label className="h4" htmlFor="locationInput">
            Location
          </label>
          <input
            type="textarea"
            className="form-control"
            id="locationInput"
            placeholder="Location..."
            value={location}
            onChange={(e) => setLocation(e.target.value)}
          />
          <button type="submit" className="btn btn-warning">
            Submit
          </button>
          <button type="button" className="btn btn-success m-3" onClick={handleReset}>
            Reset
          </button>
        </form>
      </div>
    </>
  );
}

export default NewGrievance;
