import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './complaint.css';
import Adminsidebar from '../Sidebar/adminsidebar.jsx';

function Complaint({ handle_view }) {
  const navigate = useNavigate();
  const [refresh, setRefresh] = useState(false);
  const [problems, setProblems] = useState([]);
  const [currId, setCurrId] = useState(null);
  const [show, setShow] = useState(false);
  const [status, setStatus] = useState('');

  useEffect(() => {
    const fetchProblems = async () => {
      try {
        const user = JSON.parse(localStorage.getItem('super'));
        const { data } = await axios.post(getproblemsuper);
        setProblems(data.problem.filter(problem => problem.status === 'pending'));
        setRefresh(true);
      } catch (error) {
        console.error('Error fetching problems:', error);
      }
    };
    
    if (localStorage.getItem('super')) {
      fetchProblems();
    } else {
      navigate('/superadmin/dashboards-a/1');
    }
  }, [refresh, navigate]);

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await axios.post(status_update, { status, id: currId });
      if (data.success) {
        await setShow(false);
        await setRefresh(!refresh);
        window.location.reload();
      } else {
        console.error('Failed to update status');
      }
    } catch (error) {
      console.error('Error updating status:', error);
    }
  };

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleEditClick = (itemId) => {
    setCurrId(itemId);
    handleShow();
  };

  const handleViewClick = (image) => {
    handle_view(image);
    navigate('/image');
  };

  const [showSidebar, setShowSidebar] = useState(false);

  const toggleSidebar = () => setShowSidebar(!showSidebar);

  return (
    <>
      <Adminsidebar showSidebar={showSidebar} toggleSidebar={toggleSidebar} />
      <div className="mfs-list-table-container">
        <table className="mfs-list-table">
          <thead>
            <tr>
              <th>Token</th>
              <th>Complaint Lodged By</th>
              <th>Date</th>
              <th>Department</th>
              <th>Complaint</th>
              <th>Location</th>
              <th>Status</th>
              <th>Image</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {problems.map(problem => (
              <tr key={problem._id}>
                <td>{problem.token}</td>
                <td>{problem.user.email}</td>
                <td>{new Date(problem.createdAt).toLocaleString()}</td>
                <td>{problem.department}</td>
                <td>{problem.problem}</td>
                <td>{problem.location}</td>
                <td>{problem.status}</td>
                <td>
                  <button onClick={() => handleViewClick(problem.Image)}>View</button>
                </td>
                <td>
                  <div className="icons-handle">
                    <button className="btn btn-warning" onClick={() => handleEditClick(problem._id)}>
                      Open
                    </button>
                    {show && (
                      <div className="openform">
                        <form onSubmit={handleEditSubmit}>
                          <label htmlFor="">Remarks</label>
                          <input
                            type="text"
                            name="status"
                            placeholder="Write it here.."
                            value={status}
                            onChange={(e) => setStatus(e.target.value)}
                          />
                          <div className="btns">
                            <button type="button" className="btn btn-danger" onClick={handleClose}>
                              Close
                            </button>
                            <button type="submit" className="btn btn-success">
                              Submit
                            </button>
                          </div>
                        </form>
                      </div>
                    )}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
}

export default Complaint;
