import React, { useState } from 'react';
import "./auth.css";
import { Link, useNavigate } from 'react-router-dom';
import { registerAPI } from '../utils/ApiRequest';
import { ToastContainer, toast } from 'react-toastify';
import axios from "axios";

function Register() {
  const navigate = useNavigate();
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [cpassword, setCPassword] = useState('');

  const toastOptions = {
    position: "bottom-right",
    autoClose: 2000,
    hideProgressBar: false,
    closeOnClick: true,
    pauseOnHover: false,
    draggable: true,
    progress: undefined,
    theme: "dark",
  }

  const validate = async (e) => {
    e.preventDefault();
    if (password === cpassword) {
      try {
        const response = await axios.post(registerAPI, { name, email, password });
        const { success, message, user } = response.data;
        if (success) {
          localStorage.setItem("user", JSON.stringify(user));
          toast.success(message, toastOptions);
          navigate("/user-main");
        } else {
          toast.error(message, toastOptions);
        }
      } catch (error) {
        console.error("Registration error:", error);
        toast.error("Registration failed. Please try again later.", toastOptions);
      }
    } else {
      alert('Password not matched');
    }
  }

  return (
    <section>
      <div className="container-login">
        <div className="user signinBx">
          <div className="imgBx"><img src="https://42f2671d685f51e10fc6-b9fcecea3e50b3b59bdc28dead054ebc.ssl.cf5.rackcdn.com/illustrations/reading_0re1.svg" alt="Signup" /></div>
          <div className="formBx">
            <form method="POST">
              <div className="form">
                <h2>Create an account</h2>
                <input type="text" placeholder="Username" value={name} onChange={(e) => setName(e.target.value)} />
                <input type="email" placeholder="Email Address" value={email} onChange={(e) => setEmail(e.target.value)} />
                <input type="password" placeholder="Create Password" autoComplete="on" value={password} onChange={(e) => setPassword(e.target.value)} />
                <input type="password" placeholder="Confirm Password" autoComplete="on" value={cpassword} onChange={(e) => setCPassword(e.target.value)} />
                <button type="submit" className="btn btn-success" onClick={validate}>Sign up</button>
                <p className="signup">
                  Already have an account? <Link to="/login">Sign In.</Link>
                </p>
              </div>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Register;
